mapper.update:
UPDATE t_project_information
SET companyIntroduceVideo = #{video}
WHERE projectId = #{projectId}

mapper.select:
SELECT companyIntroduceVideo
FROM t_project_information
WHERE projectId = #{projectId}

mapper.update:
UPDATE t_project_information
SET companyIntroduceText = #{text}
WHERE projectId = #{projectId}

mapper.select:
SELECT companyIntroduceText
FROM t_project_information
WHERE projectId = #{projectId}

mapper.delete:
DELETE pic
FROM t_brand_company
WHERE projectId = #{projectId} AND pic = #{pic}
